package me.iangry.trollingfreedom.commands;import org.bukkit.ChatColor;import org.bukkit.entity.Player;import org.bukkit.event.Listener;public class OP implements Listener {    public static void FakeOP(Player p) {        String p2 = p.getName();        p.sendMessage(ChatColor.translateAlternateColorCodes('&', "&7&o[Server: Made " + p2 + " a server operator]"));    }    public static void FakeDeOP(Player p) {        String p2 = p.getName();        p.sendMessage(ChatColor.translateAlternateColorCodes('&', "&7&o[Server: Made " + p2 + " no longer a server operator]"));    }}